mapper.select:
/*Review - reviewDetial */
SELECT rv_no, rv_name, rv_title, rv_content,
to_char(rv_date, 'YYYY-MM-DD HH24:MI:SS') AS rv_date, 
to_char(rv_update, 'YYYY-MM-DD HH24:MI:SS') AS rv_update, 
rv_file	FROM review WHERE rv_no = #{rv_no}

/*Review - pwdConfirm */
SELECT NVL((SELECT 1 FROM review 
WHERE rv_no=#{rv_no} AND rv_pwd=#{rv_pwd}),0) as state FROM dual

mapper.update:
/*Review - updateForm */
UPDATE review SET rv_title=#{rv_title}, rv_content=#{rv_content}, rv_update=sysdate
,rv_pwd=#{rv_pwd} 
,rv_file=#{rv_file} WHERE rv_no=#{rv_no}
